import React, { Component } from 'react';
import './index.css';

class App extends React.Component {
  state = {
    isLoading: true,
    facts: [],
    error: null
  }

  render() {
    <React.Fragment>
    </React.Fragment>
  }

  render() {
    const { isLoading, facts, error } = this.state;
    return (
      <React.Fragment>
